import React, { useState } from 'react';
import Nav from './components/Nav';
// import About from './components/About';
import Gallery from './components/Gallery';
import AboutForm from './components/About';

function App() {
  const [categories] = useState([
    {
      name: 'frontend',
      description: 'A showcase of my front end development projects.',
    },
    { name: 'backend', description: 'A showcase of my back end development projects' },
    { name: 'resume', description: <iframe src='/Resume/Resume.pdf' width='100%' height='1000'></iframe>},
  ]);

  const [currentCategory, setCurrentCategory] = useState(categories[0]);

  const [aboutSelected, setAboutSelected] = useState(false);

  const [resumeSelected, setResumeSelected] = useState(false)

  return (
    <div>
      <Nav
        categories={categories}
        setCurrentCategory={setCurrentCategory}
        currentCategory={currentCategory}
        aboutSelected={aboutSelected}
        setAboutSelected={setAboutSelected}
        resumeSelected={resumeSelected}
        setResumeSelected={setResumeSelected}
      ></Nav>
      <main>
        {!aboutSelected ? (
          <>
            <Gallery currentCategory={currentCategory}></Gallery>
          </>
        ) : (
          <AboutForm></AboutForm>
        )}
      </main>
    </div>
  );
}

export default App;
